      quote = quote.substring(1);
    }
    if (lastDoubleQuote_CharValues.includes(quote.charCodeAt(quote.length -1))) {
      quote = quote.substring(0, (quote.length -1));
    }
    quote.replace("\"", "");

    return quote;
  }

  function checkCitationInList(quote, citation) {
    let citationObj = undefined;
    console.log(quote);

    for (let i = 0; i < dataEntriesList.length; i++) {
      let key = Object.keys(dataEntriesList[i]);

      if (quote === key) {
        let values = dataEntriesList[quote];

        if (values.length === 2) {
          citationObj = values[0];
          let citeKey = Object.keys(citationObj);

          if (citeKey === citation) {
            console.log("Citation exists in data!");
            return citationObj;
          }

        } else if (values.length === 1) {
          console.log("How?");

        } else {

        }
      } else {
        console.log("No quote for citation!");
      }
    }

    return citationObj;
  }

  function checkQuoteInList(quote) {
    quote = stripQuotations(quote);

    for (let i = 0; i < dataEntriesList.length; i++) {
      let key = Object.keys(dataEntriesList[i]);
      key = JSON.stringify(key);
      console.log(typeof key + typeof quote);
      console.log(key +"\n" +quote);
      console.log(key === quote);

      if (quote === key) {
        return true;
      }
    }
    return false;
  }

  /* For now, there should only be one quote per citation! This will flip some time soon*/
  function addCitation(quote, citation) {
    // console.log("Adding citation to array");
    quote = stripQuotations(quote);
    let hasQuote = checkQuoteInList(quote);
    let citationObj = checkCitationInList(quote, citation);
    let quoteErrors = getQuoteErrors(quote);
    console.log("PRINTING");
    console.log(quoteErrors);
    let values = citationObj;

    if (hasQuote) {
      if (citationObj === undefined) {
        console.log("HERE?");

        if (values === undefined) { // Initialize the value for the citation
          citationObj = {};
          citationObj[citation] = [];
          values = [];
          values.unshift(citationObj);
          // console.log("Checking: " +citationObj[values][0])
        } else {
          citationObj = {};
          citationObj[citation] = [];
          values.unshift(citationObj);
        }

        dataEntriesList[quote] = values;

        console.log(values);
        // quotesList.quote = values;
        for (let [key, value] of Object.entries(citationObj)) {
          console.log("Key: " +key+ " Value: " +value);
        }

      } else {
        console.log("There is no quote for this citation!");
        // Error
