  function findMlaQuotes(startingIndex, inputSections, sectionWorksCited) {
    // console.log(startingIndex+ " " +inputSections+ " " +sectionWorksCited);

    for (let secIndex = startingIndex; secIndex < sectionWorksCited; secIndex++) {
      // console.log("Section index: " +secIndex);
      let currentSection = inputSections[secIndex].replace('\t', "");
      const wordsInSection = currentSection.split(" ");
      // console.log(wordsInSection.length);
      let containsQuoteInSection = false;

      /* Time to find the quotes in a section*/
      for (let i = 0; i < wordsInSection.length; i++) {
        let word = wordsInSection[i];
        // console.log("Char code? " +word+ " " +word.charCodeAt((word.length -1)));
        // console.log(word);
        let containsQuote = false;
        let quote;
        let error;

        if (firstDoubleQuote_CharValues.includes(word.charCodeAt(0))) { // First quoted word found, now find the last.
          // console.log("Checking: " +word);
          let wordsInQuote = [];
          wordsInQuote.push(word);
          let containsQuotationError = false;

          while (true) { // Checking word with ending quotation marks!
            i++;
            word = wordsInSection[i];

            const containsFinalQuote = (lastDoubleQuote_CharValues.includes(word.charCodeAt(word.length -1)));
            wordsInQuote.push(word);

            if (containsFinalQuote) {
              containsQuoteInSection = true;
              containsQuote = true;
              break;
            } else if (word.includes("(")) { // If a citation comes before an ending parenthesis, tell the user.
              error = "Missing ending parenthesis. Is it possible that a parenthesis was added inside of a quote?";
              containsQuotationError = true;
              break;
            } else if (i === (wordsInSection.length -1)) { // If this is the last word in the section and doesn't contain any quotes, tell the user.
              error = "Missing ending quotation marks!";
              containsQuotationError = true;
              break;
            }
          }

          quote = wordsInQuote.join(" ");
          quote = stripQuotations(quote); // Strip the quote because it's not necessary anymore.
          addQuote(quote);

          if (containsQuotationError) { // If there is a quotation error, go back 1 word and give error to almost made quotation!
            i--;
            addQuoteError(quote, error);
          }

          let startedCitationIndex = i;
          let containsCitation = false;
          let citation = [];

          // Get the citing of the quote after the quotation marks.
          while (true) {
            i++;
            word = wordsInSection[i];
            // console.log(word);

            citation = findMlaCitations(word, startedCitationIndex+1, i, citation);

            if (citation[0].includes(")") && citation[0].includes("(")) { // If citation was found
              containsCitation = true;
              break;
            } else if (i === (wordsInSection.length -1)) { // If last word in section and no citation found, break...
              addQuoteError(quote, "No citation found for quote");
              break;
            }
          }

          if (citation !== undefined) {
            citation = citation[0];
          }

          if (containsCitation && containsQuote) {
            addCitation(quote, citation);
            // console.log("Citations: " +citations);
          } else if (containsCitation) {
            addDocumentError("No quote exists for the given citation", citation);
            console.log("Citation errors");
            console.log(documentErrorList);
          } else {
            let quoteKey = (Object.keys(dataEntriesList.length -1));
            console.log("Quote: " +quoteKey+ " does not have a citation");
          }

          /* Add Citation error if there is one!*/

        }
      }

      if (!containsQuoteInSection) { // If the section does not contain a quote. Could it be a block citation?
        console.log("Trying block quote");
        let isBlockCitation = findMlaBlockCitation(wordsInSection);

        if (isBlockCitation) {
          console.log("It is a Block Citation");
        } else {
          console.log("No citations in this paragraph! :)");
        }
      }
    }
  }

