import './App.css';

function App() {

  let quotes = [];
  const firstDoubleQuote_CharVal = 8220;
  const lastDoubleQuote_CharVal = 8221;

  function findQuotes(startingIndex, inputSections, inputWords, sectionWorksCited) {

    for (let secIndex = startingIndex; secIndex < sectionWorksCited; secIndex++) {

      /* Time to find the quotes in a section*/
      for (let i = 0; i < inputWords.length; i++) {
        let word = inputWords[i];
        // console.log(word);

        if (word.charCodeAt(0) === firstDoubleQuote_CharVal) { // First quoted word found, now find the last.
          console.log("Checking: " +word);
          let wordsInQuote = [];
          wordsInQuote.push(word);

          while (true) { // Checking word with ending quotation marks!
            i++;
            word = inputWords[i];
            const containsFinalQuote = (word.charCodeAt(word.length -1) === lastDoubleQuote_CharVal);
            wordsInQuote.push(word);

            if (containsFinalQuote) {
              console.log("Correct word! It was: " +word);
              break;
            }
          }

          quotes.push(wordsInQuote.join(" "));

          // Get the citing of the quote after the quotation marks.
          // while (true) {
          //   i++;
          //   word = inputWords[i];

          //   // Check if the next word is the citing.
          //   if (word.includes("(")) {
          //   } else { // If the word doesn't contain a parenthesis, find the next quote.

          //   }

          // }

        }
      }
    }
    console.log(quotes);
  }

  function checkTextHandler(e) {
    e.preventDefault();

    const inputText = document.getElementById("inputText").value;
    console.log(inputText);

    if (inputText === undefined) {
      return;
    }

    const inputSections = inputText.split("\n");
    console.log("Number of sections: " +inputSections.length);

    /* Find the starting paragraph to parse! */
    let startParsingIndex = 0;
    for (let i = 0; i < inputSections.length; i++) {
      if (inputSections[i].length > 50) {
        startParsingIndex = i;
        break;
      }
    }

    console.log("Starting section: " +(startParsingIndex+1));
    /* If the starting section isn't 5, you are missing headers or added too many header/title lines */
    if (startParsingIndex < 5) {
      console.log("User is missing header information");
      return;
    } else if (startParsingIndex > 5) {
      console.log("User added too many header/title lines before introduction of paper");
      return;
    }

    const inputWords = inputSections[startParsingIndex].split(" ");

    let sectionWorksCited;
    /* Check where the Works Cited page might exist! */
    for (let sectionNum = startParsingIndex; sectionNum < inputSections.length; sectionNum++) {
      if (inputSections[sectionNum].length < 20) {
        sectionWorksCited = inputSections[sectionNum];
        console.log("Possible Works Cited page on section " +(sectionNum+1)+ "?");
      }
    }

    findQuotes(startParsingIndex, inputSections, inputWords, sectionWorksCited);
  }

  return (
    <div className="App">
      <div className="document">
        <textarea id="inputText" className="inputText"/>
        <button onClick={checkTextHandler}>Check Text</button>
      </div>
    </div>
  );
}

export default App;
