import './App.css';

function App() {

  let quotes = [];
  let citations = [];
  let citation = []; // HMmm....
  const firstDoubleQuote_CharVal = 8220;
  const lastDoubleQuote_CharVal = 8221;

  // Need Valid MLA inline-citation function

  function addMlaBlockQuote(wordsInSection, lastIndex) {
    let wordsInQuote = [];

    for (let i = 0; i < lastIndex; i++) {
      const word = wordsInSection[i];
      wordsInQuote.push(word);
    }

    quotes.push(wordsInQuote.join(" "));
  }

  function removeMlaDecimal(word) {
    const chars = word.split('');
    if (chars[(word.length-1)] === '.') { // If the last character is a decimal, remove it!
      // console.log("TRUE! Removing decimal");
      const result = word.slice(0, -1);
      return result;
    } else { // Else, keep the word!
      return word;
    }
  }

  function findMlaCitations(word, startCitationIndex, i) {

    if (!word.includes("(") && (startCitationIndex === i)) { // If beginning of citation and doesn't include open parenthesis.
      console.log("Citation does not come right after quote");
      // Do more...

      // If citing doesn't come right after quote, check if it's later in the same section.
      // while (true) {
      //   i++;
      //   word = inputWords[i];

      //   // Check if the next word is the citing.
      //   if (word.includes("(")) {


      //   } else { // If the word doesn't contain a parenthesis, find the next quote.

      //   }

      // }

    } else if (word.includes("(")) { // If citation is right after quote.
      word.replace(/[(]/g, ''); //Replaces start parenthesis with empty.
      // console.log(word);

      if (word.includes(")")) { // If word is also end of citation too.
        word.replace(/[)]/g, '');
        // console.log(word);
        const finalWord = removeMlaDecimal(word);
        citation.push(finalWord);
        return true;
      }
      if (word.includes(",")) { // If the word has a comma, remove it
        word.replace(/[,]/g, '');
      }
      citation.push(word);

    } else if (word.includes(")")) { // If this is the end of the citation.
      word.replace(/[)]/g, '');
      // console.log(word);
      const finalWord = removeMlaDecimal(word);
      citation.push(finalWord);
      return true;

    } else {
      // console.log(word);
      citation.push(word);
    }

    return false;
  }

  function findMlaQuotes(startingIndex, inputSections, sectionWorksCited) {
    // console.log(startingIndex+ " " +inputSections+ " " +sectionWorksCited);

    for (let secIndex = startingIndex; secIndex < sectionWorksCited; secIndex++) {
      console.log("Section index: " +secIndex);
      const wordsInSection = inputSections[secIndex].split(" ");
      // console.log(wordsInSection);

      let containsQuote = false;

      /* Time to find the quotes in a section*/
      for (let i = 0; i < wordsInSection.length; i++) {
        let word = wordsInSection[i];
        // console.log(word);

        if (word.charCodeAt(0) === firstDoubleQuote_CharVal) { // First quoted word found, now find the last.
          // console.log("Checking: " +word);
          let wordsInQuote = [];
          wordsInQuote.push(word);

          while (true) { // Checking word with ending quotation marks!
            i++;
            word = wordsInSection[i];
            const containsFinalQuote = (word.charCodeAt(word.length -1) === lastDoubleQuote_CharVal);
            wordsInQuote.push(word);

            if (containsFinalQuote) {
              containsQuote = true;
              // console.log("Correct word! It was: " +word);
              break;
            } else if (word.includes("(")) { // If a citation comes before an ending parenthesis, tell the user.
              console.log("Missing ending parenthesis. Is it possible that a parenthesis was added inside of a quote?");

            } else if (i === wordsInSection[(wordsInSection -1)]) { // If this is the last word in the section and doesn't contain any quotes, tell the user.
              console.log("Missing quotation marks!");
            }
          }

          quotes.push(wordsInQuote.join(" "));

          let startCitationIndex = i;
          let containsCitation = false;

          // Get the citing of the quote after the quotation marks.
          while (true) {
            i++;
            word = wordsInSection[i];
            // console.log(word);

            if (word === undefined) { // Break if this is the end of the word in the section.
              break;
            }

            const foundWord = findMlaCitations(word, startCitationIndex, i);

            if (foundWord) {
              console.log("Citation found");
              containsCitation = true;
              break;
            } else if (i === (wordsInSection -1)) {
              console.log("No citation found for quote...");
              break;
            }

          }

          if (containsCitation) {
            citations.push(citation.join(" "));
            console.log("Citations: " +citations);
          } else {
            let mostRecentQuoteIndex = (quotes.length -1);
            console.log("Quote: " +quotes[mostRecentQuoteIndex]+ " does not have a citation");
          }

          citation = []; // AAAGHHH!! DON'T FORGET TO RESET THIS!!!

        }
      }

      if (!containsQuote) { // If the section does not contain a quote. Could it be a block citation?
        let containsCitation = false;

        if (wordsInSection[wordsInSection.length -1].includes(")")) { // Checks if last word contains ending parenthesis. Possible block citation!

          /* Going backwards in this section! :) */
          for (let j = (wordsInSection.length -1); j > 0; j--) {
            let word = wordsInSection[j];

            if (word.includes("(")) { // If has opening paren, then citation exists!
              console.log("Block section!");
              containsCitation = true;
              citation.unshift(word);
              addMlaBlockQuote(wordsInSection, j);
              break;
            }
            citation.unshift(word);
          }

        }

        if (containsCitation) {
          citations.add(citation);
          console.log("Citations: " +citations);
        } else {
          console.log("No citations in this paragraph! :)");
        }

        citation = []; // AAAGHHH!! DON'T FORGET TO RESET THIS!!!
      }
    }
    console.log("Quotes: " +quotes);
  }

  function checkMlaHandler(e) {
    e.preventDefault();

    const inputText = document.getElementById("inputText").value;
    // console.log(inputText);

    if (inputText === undefined) {
      return;
    }

    const inputSections = inputText.split("\n");
    // console.log("Number of sections: " +inputSections.length);

    /* Find the starting paragraph to parse! */
    let startParsingIndex = 0;
    for (let i = 0; i < inputSections.length; i++) {
      if (inputSections[i].length > 50) {
        startParsingIndex = i;
        break;
      }
    }

    // console.log("Starting section: " +(startParsingIndex+1));

    /* If the starting section isn't 5, you are missing headers or added too many header/title lines */
    if (startParsingIndex < 5) {
      console.log("User is missing header information");
      return;
    } else if (startParsingIndex > 5) {
      console.log("User added too many header/title lines before introduction of paper");
      return;
    }

    let sectionWorksCited;
    /* Check where the Works Cited page might exist! */
    for (let sectionNum = startParsingIndex; sectionNum < inputSections.length; sectionNum++) {
      if (inputSections[sectionNum].split(" ").length === 2) {
        sectionWorksCited = sectionNum;
        // console.log("Possible Works Cited page on section " +(sectionNum+1)+ "?");
      }
    }

    findMlaQuotes(startParsingIndex, inputSections, sectionWorksCited);
  }

  return (
    <div className="App">
      <div className="document">
        <textarea id="inputText" className="inputText"/>
        <button onClick={checkMlaHandler}>Check Text</button>
      </div>
    </div>
  );
}

export default App;
