import './App.css';

function App() {

  function checkTextHandler(e) {
    e.preventDefault();
    const doubleQuote_CharVal = 8220

    const inputText = document.getElementById("inputText").value;
    console.log(inputText);
    const inputWords = inputText.split(" ");

    let quotes = [];
    for (let i = 0; i < inputWords.length; i++) {
      let word = inputWords[i];
      // console.log(word + " " +charCode);

      if (word.charCodeAt(0) === doubleQuote_CharVal) { // First quoted word found, now find the last.
        console.log("Checking: " +word);
        let wordsInQuote = [];
        wordsInQuote.push(word);

        while (true) { // Checking word with ending quotation marks!
          i++;
          word = inputWords[i];
          console.log(word);
          const lastIndex = word.length - 1;
          const containsFinalQuote = word.charCodeAt(lastIndex) === doubleQuote_CharVal;

          if (containsFinalQuote) {
            console.log("Correct word! It was: " +word);
            wordsInQuote.push(word);
            break;
          }
          wordsInQuote.push(word);
        }

        quotes.push(wordsInQuote.split());
      }
    }
    console.log(quotes);
  }

  return (
    <div className="App">
      <div className="document">
        <textarea id="inputText" className="inputText"/>
        <button onClick={checkTextHandler}>Check Text</button>
      </div>
    </div>
  );
}

export default App;
